mapper.select:
SELECT row_number() OVER (ORDER BY ee.entidad) AS id, ee.entidad, ee.region_edomex_2017 region, ee.nombre_region, ee.municipio, ee.nombre_municipio, ee.seccion, COALESCE(SUM(CASE WHEN r.id_contacto IS NOT NULL AND r.estatus='EXITOSO' THEN 1 ELSE 0 END),0) exitoso, COALESCE(SUM(CASE WHEN r.id_contacto IS NOT NULL AND r.estatus='NO_HUBO_QUIEN_CONTESTARA' THEN 1 ELSE 0 END),0) no_hubo_quien_contestara, COALESCE(SUM(CASE WHEN r.id_contacto IS NOT NULL AND r.estatus='MENOR_DE_EDAD' THEN 1 ELSE 0 END),0) menor_de_edad, COALESCE(SUM(CASE WHEN r.id_contacto IS NOT NULL AND r.estatus='DOMICILIO_NO_ENCONTRADO' THEN 1 ELSE 0 END),0) domicilio_no_encontrado, COALESCE(SUM(CASE WHEN r.id_contacto IS NOT NULL AND r.estatus='NO_QUISIERON_CONTESTAR' THEN 1 ELSE 0 END),0) no_quisieron_contestar, COALESCE(SUM(CASE WHEN r.id_contacto IS NOT NULL AND r.estatus='OTRO' THEN 1 ELSE 0 END),0) otro, COALESCE(SUM(CASE WHEN r.id_contacto IS NOT NULL THEN 1 ELSE 0 END),0) total, COALESCE((ems.contactos),0) totalTotal, COALESCE((ems.meta),0) meta FROM (SELECT id_contacto,estatus FROM levantamiento_rowan.e_edomex) r RIGHT JOIN levantamiento_rowan.encuesta_edomex ee ON (ee.id_interno=r.id_contacto::INTEGER) RIGHT JOIN levantamiento_rowan.e_edomex_secciones ems ON (ems.llave_seccion=(ee.entidad||'-'||ee.seccion))
ee.municipio IN
#{item.idMunicipio}
ee.region_edomex_2017 IN
#{item.idParticion}
GROUP BY ee.entidad, ee.region_edomex_2017, ee.nombre_region, ee.municipio, ee.nombre_municipio, ee.seccion, ems.contactos, ems.meta ORDER BY ee.entidad, ee.region_edomex_2017, ee.nombre_region, ee.municipio, ee.nombre_municipio, ee.seccion, ems.contactos, ems.meta

SELECT row_number() OVER (ORDER BY ee.entidad) AS id, ee.entidad, ee.region_edomex_2017 region, ee.nombre_region, ee.municipio, ee.nombre_municipio, COUNT(DISTINCT(ee.seccion)) num_seccion, COALESCE(SUM(CASE WHEN r.id_contacto IS NOT NULL AND r.estatus='EXITOSO' THEN 1 ELSE 0 END),0) exitoso, COALESCE(SUM(CASE WHEN r.id_contacto IS NOT NULL AND r.estatus='NO_HUBO_QUIEN_CONTESTARA' THEN 1 ELSE 0 END),0) no_hubo_quien_contestara, COALESCE(SUM(CASE WHEN r.id_contacto IS NOT NULL AND r.estatus='MENOR_DE_EDAD' THEN 1 ELSE 0 END),0) menor_de_edad, COALESCE(SUM(CASE WHEN r.id_contacto IS NOT NULL AND r.estatus='DOMICILIO_NO_ENCONTRADO' THEN 1 ELSE 0 END),0) domicilio_no_encontrado, COALESCE(SUM(CASE WHEN r.id_contacto IS NOT NULL AND r.estatus='NO_QUISIERON_CONTESTAR' THEN 1 ELSE 0 END),0) no_quisieron_contestar, COALESCE(SUM(CASE WHEN r.id_contacto IS NOT NULL AND r.estatus='OTRO' THEN 1 ELSE 0 END),0) otro, COALESCE(SUM(CASE WHEN r.id_contacto IS NOT NULL THEN 1 ELSE 0 END),0) total, COALESCE((ems.contactos),0) totalTotal, COALESCE((ems.meta),0) meta FROM (SELECT id_contacto,estatus FROM levantamiento_rowan.e_edomex) r RIGHT JOIN levantamiento_rowan.encuesta_edomex ee ON (ee.id_interno=r.id_contacto::INTEGER) RIGHT JOIN levantamiento_rowan.e_edomex_municipios ems ON (ems.llave_municipio=(ee.entidad||'-'||ee.municipio))
ee.municipio IN
#{item.idMunicipio}
ee.region_edomex_2017 IN
#{item.idParticion}
GROUP BY ee.entidad, ee.region_edomex_2017, ee.nombre_region, ee.municipio, ee.nombre_municipio, ems.contactos, ems.meta ORDER BY ee.entidad, ee.region_edomex_2017, ee.nombre_region, ee.municipio, ee.nombre_municipio, ems.contactos, ems.meta

SELECT row_number() OVER (ORDER BY ee.entidad) AS id, ee.entidad, ee.region_edomex_2017 region, ee.nombre_region, COUNT(DISTINCT(ee.municipio)) num_municipio, COUNT(DISTINCT(ee.seccion)) num_seccion, COALESCE(SUM(CASE WHEN r.id_contacto IS NOT NULL AND r.estatus='EXITOSO' THEN 1 ELSE 0 END),0) exitoso, COALESCE(SUM(CASE WHEN r.id_contacto IS NOT NULL AND r.estatus='NO_HUBO_QUIEN_CONTESTARA' THEN 1 ELSE 0 END),0) no_hubo_quien_contestara, COALESCE(SUM(CASE WHEN r.id_contacto IS NOT NULL AND r.estatus='MENOR_DE_EDAD' THEN 1 ELSE 0 END),0) menor_de_edad, COALESCE(SUM(CASE WHEN r.id_contacto IS NOT NULL AND r.estatus='DOMICILIO_NO_ENCONTRADO' THEN 1 ELSE 0 END),0) domicilio_no_encontrado, COALESCE(SUM(CASE WHEN r.id_contacto IS NOT NULL AND r.estatus='NO_QUISIERON_CONTESTAR' THEN 1 ELSE 0 END),0) no_quisieron_contestar, COALESCE(SUM(CASE WHEN r.id_contacto IS NOT NULL AND r.estatus='OTRO' THEN 1 ELSE 0 END),0) otro, COALESCE(SUM(CASE WHEN r.id_contacto IS NOT NULL THEN 1 ELSE 0 END),0) total, COALESCE((ems.contactos),0) totalTotal, COALESCE((ems.meta),0) meta FROM (SELECT id_contacto, estatus FROM levantamiento_rowan.e_edomex) r RIGHT JOIN levantamiento_rowan.encuesta_edomex ee ON (ee.id_interno=r.id_contacto::INTEGER) RIGHT JOIN levantamiento_rowan.e_edomex_regiones ems ON (ems.region=ee.region_edomex_2017)
ee.municipio IN
#{item.idMunicipio}
ee.region_edomex_2017 IN
#{item.idParticion}
GROUP BY ee.entidad, ee.region_edomex_2017, ee.nombre_region, ems.contactos, ems.meta ORDER BY ee.entidad, ee.region_edomex_2017, ee.nombre_region, ems.contactos, ems.meta
