mapper.select:
select trunc(random() * 1000000 + 1)::integer fila_r,
region,
region, municipio,
region, zona,
region, municipio, zona, seccion,
region, municipio, zona, seccion, usuario, nombre_completo, imei,
sum(completa) completa, sum(imcompleta) incompleta, sum(nego_dar_informacion) nego_dar_informacion, sum(no_tiene_credencial) no_tiene_credencial, sum(no_se_encontro_persona) no_se_encontro_persona, sum(no_se_encontro_vivienda) no_se_encontro_vivienda,sum(no_hubo_quien_respondiera) no_hubo_quien_respondiera, sum(sin_estado) sin_estado from( select region, municipio, zona, seccion,usuario, c.nombre||' '||c.primerapellido||' '||c.segundoapellido nombre_completo,
a.imei,
case when codigo_resultado=1 then 1 else 0 end completa, case when codigo_resultado=2 then 1 else 0 end imcompleta, case when codigo_resultado=3 then 1 else 0 end nego_dar_informacion, case when codigo_resultado=4 then 1 else 0 end no_tiene_credencial, case when codigo_resultado=5 then 1 else 0 end no_se_encontro_persona, case when codigo_resultado=6 then 1 else 0 end no_se_encontro_vivienda, case when codigo_resultado=7 then 1 else 0 end no_hubo_quien_respondiera, case when codigo_resultado=8 then 1 else 0 end sin_estado from encuesta a join usuario c on (a.usuario_id = c.id)
where c.region = #{region}
and to_char(a.creada, 'dd/mm/yyyy') = #{fechaInicio}
and region = #{region}
and municipio = #{municipio}
and zona = #{zona}
and to_char(a.creada, 'dd/mm/yyyy') >= #{fechaInicio} and to_char(a.creada, 'dd/mm/yyyy') <= #{fechaFin}
and region = #{region}
and municipio = #{municipio}
and zona = #{zona}
where to_char(a.creada, 'dd/mm/yyyy') = #{fechaInicio}
and to_char(a.creada, 'dd/mm/yyyy') >= #{fechaInicio} and to_char(a.creada, 'dd/mm/yyyy') <= #{fechaFin}
) t
group by region order by region
group by region, municipio order by region, municipio
group by region, zona order by region, zona
group by region, municipio, zona, seccion order by region, municipio, zona, seccion
group by region, municipio, zona, seccion, usuario, nombre_completo, imei order by region, municipio, zona, seccion, usuario, nombre_completo, imei

select trunc(random() * 1000000 + 1)::integer fila_r,
region,
region, municipio,
region, zona,
region, municipio, zona, seccion,
region, municipio, zona, seccion, usuario, a.nombre||' '||a.primer_apellido||' '||a.segundo_apellido nombre_completo,
case when codigo_resultado=1 then 1 else 0 end completa, case when codigo_resultado=2 then 1 else 0 end incompleta, case when codigo_resultado=3 then 1 else 0 end nego_dar_informacion, case when codigo_resultado=4 then 1 else 0 end no_tiene_credencial, case when codigo_resultado=5 then 1 else 0 end no_se_encontro_persona, case when codigo_resultado=6 then 1 else 0 end no_se_encontro_vivienda, case when codigo_resultado=7 then 1 else 0 end no_hubo_quien_respondiera, case when codigo_resultado=8 then 1 else 0 end sin_estado, codigo_respuesta estado from encuesta a join usuario c on (a.usuario_id = c.id) join codigos_respuesta d on (a.codigo_resultado = d.id_codigo_respuesta)
where c.region = #{region} and c.usuario='${nick}'
and to_char(a.creada, 'dd/mm/yyyy') = #{fechaInicio}
and region = #{region}
and municipio = #{municipio}
and zona = #{zona}
and to_char(a.creada, 'dd/mm/yyyy') >= #{fechaInicio} and to_char(a.creada, 'dd/mm/yyyy') <= #{fechaFin}
and region = #{region}
and municipio = #{municipio}
and zona = #{zona}
where to_char(a.creada, 'dd/mm/yyyy') = #{fechaInicio} and c.usuario='${nick}'
and to_char(a.creada, 'dd/mm/yyyy') >= #{fechaInicio} and to_char(a.creada, 'dd/mm/yyyy') <= #{fechaFin}
order by region
order by region, municipio
order by region, zona
order by region, municipio, zona, seccion
order by region, municipio, zona, seccion, usuario, nombre_completo group by region, municipio, zona, seccion, usuario, imei order by region, municipio, zona, seccion, usuario, imei
